import React, { Fragment } from "react";
import { useState } from "react";

import Header from "./components/Layout/Header";
import Meals from "./components/Meals/Meals";
import Cart from "./components/Cart/Cart";
import CartProvider from "./store/CartProvider";
import Registration from "./components/Auth/Registration"; // Import the Registration component

import LoginHandler from "./components/Auth/login";
function App() {
  const [cartIsShown, setCartIsShown] = useState(false);
  const [user, setUser] = useState(null);

  const showCartHandler = () => {
    setCartIsShown(true);
  };

  const hideCartHandler = () => {
    setCartIsShown(false);
  };
  const handleLogin = () => {
    // Set the user as logged in
    setUser(true);
  };

  const mainAppFunctionality = (
    <CartProvider>
      {cartIsShown && <Cart onClose={hideCartHandler} />}

      <Header onShowCart={showCartHandler} />
      <main>
        <Meals />
      </main>
    </CartProvider>
  );

  return (
    <Fragment>
      {user ? (
        { mainAppFunctionality }
      ) : (
        <React.Fragment>
          <Registration onLogin={handleLogin} />
          <LoginHandler onLogin={handleLogin} />
        </React.Fragment>
      )}
    </Fragment>
  );
}

export default App;
