import MealsSummary from "./MealsSummary";
import AvailableMeals from "./AvailableMeals";
import { Fragment, useState } from "react";
import Card from "../UI/Card";
import classes from "./Meals.module.css";
import MealTab from "../UI/MealTab";
import '../UI/MealTab.css'


const Meals = () => {
    const [activeTab, setActiveTab] = useState('Starters');


    const handleTabChange = (tab) => {
        setActiveTab(tab);
        console.log(activeTab=='Starters')
      };
    


    const renderTabContent = () => {
        switch (activeTab) {
          case 'Starters':
            return <div>This is the content of Starters.</div>;
          case 'Salads':
            return <div>This is the content of salads</div>
          case 'Main':
            return <div>This is the content of Main.</div>;
          case 'Desserts':
            return <div>This is the content of Desserts.</div>;
          default:
            return null;
        }
      };
  return (
    <Fragment>
      <MealsSummary />
      <MealTab
          tabs={['Starters', 'Salads', 'Main', 'Desserts']}
          defaultTab="Main"
          onTabChange={handleTabChange}
        />
        {renderTabContent()}
      <AvailableMeals />
    </Fragment>
  );
};
export default Meals;
